function updateSelector() {
    var div = document.getElementById("myselectpicker");
    var divlength = div.options.length;
    for(var i=0; i < divlength; i++){
        console.log("remove");
        div.removeChild(div.firstChild);
    }
    if(document.getElementById('cbExcludeGeo').checked){
        var xbase = enx.slice()
        var mydata = [
            {
                x: [],
                y: [],
                type: 'bar'
            }
        ];

    } else {
        var xbase = x.slice();
        var mydata = [
            {
                x: [],
                y: [],
                type: 'bar'
            }
        ];
    }
    for (var i = 0; i<xbase.length; i++) {
        console.log("add");
        var option = document.createElement("option");
        option.appendChild(document.createTextNode(xbase[i]));
        div.appendChild(option);
    }
    $('.selectpicker').selectpicker('refresh');
    Plotly.newPlot('myDiv', mydata, layout);

}

// end create selector options


// update graph when selection changes
function updateSelectedGraph() {
    var selectpicker = document.getElementById( "myselectpicker" );
    console.log(selectpicker.options[0].selected);
    var selected = [];
    if(document.getElementById('cbExcludeGeo').checked){
        var xbase = enx.slice();
        var ybase = eny.slice();

    } else {
        var xbase = x.slice();
        var ybase = y.slice();
    }
    nx = [];
    ny = [];
    for (var i = 0; i<xbase.length; i++) {
        if(selectpicker.options[i].selected){
            selected.push(i)
            nx.push(x[i]);
            ny.push(y[i]);
        }
    };
    var mydata = [
        {
            x: nx,
            y: ny,
            type: 'bar'
        }
    ];

    Plotly.newPlot('myDiv', mydata, layout);
}
// end update graph

// create graph for the first time
Plotly.newPlot('myDiv', data, layout);
// end create graph for the first time


function changeTitleTool(value) {

    var data = [
        {
            x: xsort.slice(0, value),
            y: ysort.slice(0, value),
            type: 'bar'
        }
    ];
    Plotly.newPlot('myDiv', data, layout);

}

checkRadioButtonTop();
function checkRadioButtonTop(){
    var form = document.radioButtonForm;
    console.log(form[0].checked);
    if(form[0].checked){
        document.getElementById('cbExcludeGeoLabel').style.display = 'none';
        $('.selectpicker').selectpicker('hide');
        document.getElementById('sliderInput').style.display='inline';

    } else {
        document.getElementById('cbExcludeGeoLabel').style.display = 'inline';
        $('.selectpicker').selectpicker('show');
        document.getElementById('sliderInput').style.display='none';
    }
    var value = document.getElementById('sliderInput').value;
    changeTitleTool(value);

}
function checkRadioButtonInd(){
    var form = document.radioButtonForm;
    console.log(form[0].checked);
    if(form[0].checked){
        document.getElementById('cbExcludeGeoLabel').style.display = 'none';
        $('.selectpicker').selectpicker('hide');
        document.getElementById('sliderInput').style.display='inline';

    } else {
        document.getElementById('cbExcludeGeoLabel').style.display = 'inline';
        $('.selectpicker').selectpicker('show');
        document.getElementById('sliderInput').style.display='none';
    }
    var value = document.getElementById('sliderInput').value;
    updateSelector();
    $('[data-toggle="tooltip"]').tooltip('hide');
}




















