var counter = 0;
function animateStyle(counter) {
    var htmlcollection = document.getElementsByClassName('tile_stats_count');
    var arr = Array.prototype.slice.call(htmlcollection);
    var timer = setInterval(function () {
            if (counter == arr.length) {
                arr[counter-1].style.color = 'black';
                clearInterval(timer);
            } else {
                arr[counter].style.color = 'blue';
                arr[counter].style.fontWeight = 'bold';
                if (counter >= 1){
                    arr[counter-1].style.color = 'black';
                }
            }

            counter +=1;

    }, 500);
}
setTimeout(function() { animateStyle(counter); }, 5000);
